const headersArray = $json.columns.split(',');

// Create an object where each key is a header (trimmed) and its value is the header text
const headersObject = {};
headersArray.forEach(header => {
const trimmedHeader = header.trim();
headersObject[trimmedHeader] = trimmedHeader;
});

// Return the object so that each header becomes a column in the first row of your sheet
return [
{
json: headersObject
}
];

