/**
 * Senior Engineer Logic: Flattening AI output and appending 
 * a separator row ("---") to conclude the page report.
 */

const input = $input.all();
const results = [];

for (const item of input) {
  // Point to the AI Agent's "output" wrapper
  const data = item.json.output;

  if (data && Array.isArray(data.qc_breakdown)) {
    data.qc_breakdown.forEach(entry => {

      // Handling the double-nested structure seen in the input schema
      const entryData = entry.qc_breakdown || entry;

      results.push({
        "Property/Page Name": data.url || "URL Not Found",
        "QC Parameters": entryData.category || "N/A",
        "Action Item": entryData.issue || "No issues found"
      });
    });
  }
}

// Push the visual separator row after all QC entries for the current page are added
if (results.length > 0) {
  results.push({
    "Property/Page Name": "---",
    "QC Parameters": "---",
    "Action Item": "---"
  });
}

return results;

